第一遍：徒手写的，错误连篇
package main

import "fmt"

func addTwo(nums []int, target int) []int {
  var m map[int]int
  for i, v := range nums {
    if j,ok := m[target-v]; ok {
      return [i,j]
    }
    m[v] = i
  }
  return nil
}

